module.exports = function (name, getContent) {
    if (typeof getContent === 'undefined') {
        getContent = name;
        name = '';
    }

    if (typeof getContent !== 'function') {
        var content = getContent;
        getContent = function (callback) {
            callback(content);
        };
    }

    return [{
        name: name,
        getContent: getContent
    }];
};
